<script>
import axios from 'axios';

export default {
  data : function () {
    return {
      current : 0,
      is_locked : true,
      done: 1,
      sub: 0,
    }
  },
  mounted(){
    this.m_init();
  },
  methods: {
    m_init: function () {
      if (this.is_locked) {
        let body = document.querySelector('body');

        body.classList.add('is_locked');
      }
    },
    handleScroll: function (evt, el) {
      let body      = document.querySelector('body');
      let sections  = document.querySelectorAll('.has_scroll');
      let next      = document.querySelector('.has_scroll + div:not(.has_scroll)');
      let next_top  = next.getBoundingClientRect().top
      let scroll    = Math.round(event.deltaY);

      if(!this.done){
       this.done = 0.5;

       setTimeout(() => {
         this.done = 1;
       }, 500);
      }

      if (body.getBoundingClientRect().width > 850) {
        if (this.done === 1 && this.current !== 2) {
          if (scroll > 1){
            if (this.current < sections.length - 1) {
              this.done = 0;
              this.current++;
              window.scroll({
                top: sections[this.current].getBoundingClientRect().top,
                left: 0,
                behavior: 'smooth'
              })
            }else {
              if (this.current < 3) {
                this.current++;
                if (this.current == 3) {
                  body.classList.remove('is_locked');
                }
              }
            }
          }else if (scroll < -1){
            if (this.current == 3) {
              if (Math.round(next_top) > 150) {
                body.classList.add('is_locked');
                this.current = 1;
                this.done = 0;
                window.scroll({
                  top: sections[sections.length - 1].offsetTop,
                  left: 0,
                  behavior: 'smooth'
                })
              }
            }else if (this.current > 0 && this.current < sections.length ) {
              this.done = 0;
              this.current--;
              window.scroll({
                top: sections[this.current].getBoundingClientRect().top,
                left: 0,
                behavior: 'smooth'
              })
            }else if (this.current > 0) {
              this.current--;
            }
          }
        }
        if (this.done === 1 && this.current == 2) {
          if (scroll > 1){
            if (this.current = sections.length && this.sub !== 3) {
              this.done = 0;
              this.sub++;
            }else {
              if (this.current < 3 && this.sub == 3) {
                this.current = 3;
                body.classList.remove('is_locked');
              }
            }
          }else if (scroll < -1){
            this.sub = 3;
          }
        }
      }

    },
    contact: function () {
      event.preventDefault();
      let news = document.querySelector('.home__newsletter');
      let body = document.querySelector('body');

      window.scroll({
        top: news.getBoundingClientRect().top,
        left: 0,
        behavior: 'smooth'
      })

      this.current = 3;
      this.done = 1;

      body.classList.remove('is_locked');
    },
  },
}
</script>
